import React, { Component } from 'react'

export class NewsItem extends Component {
    render() {
        let {title, description, urlImage, newsUrl } = this.props //destructuring
        return (
            <>
            <div>
                {/* one bracket for java script and one for object */}
                <div className="card" style={{width: "18rem"}}> 
                    <img src={urlImage} className="card-img-top" alt="..."/>
                    <div class ="card-body">
                    <h5 class ="card-title">{title}...</h5>
                    <p class ="card-text">{description}....</p>
                    <a href={newsUrl} rel="noreferrer" target="_blank" class ="btn btn-sm btn-primary">Read More...</a>
                    </div>
                </div> 
                </div>
                           
                </>
        )
    }
}

export default NewsItem
