import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';

export class News extends Component {

    constructor(){
        super();
        console.log("This is me");
        this.state = {
            articles: [],
            loading: false,
            page: 1,
            
        }
    }
    
    async componentDidMount(){
        let url = `https://newsapi.org/v2/everything?domains=wsj.com&apiKey=202fcca38be941fc97fb9fdc6a167cde&page=1&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles, totalResults: parsedData.totalResults})
        
    }
    
    handlePreviousClick = async ()=>{
        let url = `https://newsapi.org/v2/everything?domains=wsj.com&apiKey=202fcca38be941fc97fb9fdc6a167cde&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            articles: parsedData.articles,
            page: this.state.page - 1
        })
    }
    
    handleNextClick = async ()=>{
        if (!(this.state.page+1 > Math.ceil(this.state.totalResults/20))){
            this.state.loading = false;
            let url = `https://newsapi.org/v2/everything?domains=wsj.com&apiKey=202fcca38be941fc97fb9fdc6a167cde&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
            let data = await fetch(url);
            let parsedData = await data.json();
            this.state.loading = false;
            this.setState({
                articles: parsedData.articles,
                page: this.state.page +1
                // totalResults: this.state.totalResults
            })
        }       
    }
    render() {
    return (
        
            <>
            <div className="container my-3">
                <h1 className="text-center">This is news section.</h1>  
                <hr/>
                {this.state.loading && <Spinner/>}
            <div className="row">
                {this.state.articles.map((element)=>{
                return <div className="col-md-4 my-3" key={element.url}>
                <NewsItem title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,88):""} urlImage= {element.urlToImage} newsUrl= {element.url}/ >
                </div>

                })}
            </div>  
                        
            </div>
            <div className="container d-flex justify-content-between my-5">
                <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handlePreviousClick}> &larr; Previous</button>
                <button disabled={this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" class="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
                </>
        )
    }
}

export default News
