import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {

    constructor(){
        super();
        console.log("This is me");
        this.state = {
            articles: [],
            loading: false
        }
    }

    async componentDidMount(){
        let url = "https://newsapi.org/v2/everything?domains=wsj.com&apiKey=202fcca38be941fc97fb9fdc6a167cde&page=2";
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles})

    }

    render() {
        return (
            <>
            <div className="container my-3">
                <h1>This is news section.</h1>  
                <hr />
            <div className="row">
                {this.state.articles.map((element)=>{
                return <div className="col-md-4 my-3" key={element.url}>
                <NewsItem title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,88):""} urlImage= {element.urlToImage} newsUrl= {element.url}/ >
                </div>

                })}
            </div>  
                        
            </div>
            <div className="container d-flex justify-content-between my-6">
                <button type="button" class="btn btn-dark">Previous</button>
                <button type="button" class="btn btn-dark">Next</button>
                </div>
                </>
        )
    }
}

export default News
